let scores,roundScore,activePlayer,gamePlaying;function init(){scores=[0,0],activePlayer=0,roundScore=0,gamePlaying=!0,document.querySelector(".dice").style.display="none",document.getElementById("score-0").textContent="0",document.getElementById("score-1").textContent="0",document.getElementById("current-0").textContent="0",document.getElementById("current-1").textContent="0",document.getElementById("name-0").textContent="Player 1",document.getElementById("name-1").textContent="Player 2"}function nextPlayer(){activePlayer=0===activePlayer?1:0,roundScore=0,document.getElementById("current-0").textContent="0",document.getElementById("current-1").textContent="0",document.querySelector(".player-0-panel").classList.toggle("active"),document.querySelector(".player-1-panel").classList.toggle("active"),document.querySelector(".dice").style.display="none"}init(),document.querySelector(".btn-roll").addEventListener("click",function(){if(gamePlaying){const e=Math.floor(6*Math.random())+1,t=document.querySelector(".dice");t.style.display="block",t.src="assets/images/dice-"+e+".png",1!==e?(roundScore+=e,document.querySelector("#current-"+activePlayer).textContent=roundScore):nextPlayer()}}),document.querySelector(".btn-hold").addEventListener("click",function(){gamePlaying&&(scores[activePlayer]+=roundScore,document.querySelector("#score-"+activePlayer).textContent=scores[activePlayer],scores[activePlayer]>=100?(document.querySelector("#name-"+activePlayer).textContent="Winner!",document.querySelector(".dice").style.display="none",document.querySelector(".player-"+activePlayer+"-panel").classList.add("winner"),document.querySelector(".player-"+activePlayer+"-panel").classList.remove("active"),gamePlaying=!1):nextPlayer())}),document.querySelector(".btn-new").addEventListener("click",init);